mapper.select:
select id,username,avator from tth_user

     status = 1

         and teacher_title = #{teacher_title}


         and teacher_job = #{teacher_job}


         and username like #{username}


 limit #{begin},#{count}

select count(*) from tth_user

    status = 1

        and teacher_title = #{teacher_title}


        and teacher_job = #{teacher_job}


        and username like #{username}

mapper.update:
update tth_user
email = #{email}, number = #{number}, postal_address = #{postal_address}, achievement = #{achievement}, resume = #{resume}, teacher_title = #{teacher_title}, teacher_job = #{teacher_job}, teacher_job2 = #{teacher_job2},
where id = #{uid}

mapper.select:
select id,username,avator from tth_user

    status = 0

        and username like #{username}


limit #{begin},#{count}

select count(*) from tth_user

    status = 0

        and username like #{username}
